import React, { Component, useEffect, useRef, useState } from "react";
import {
  StyleSheet,
  View,
  Text,
  TouchableOpacity,
  ScrollView,
  TextInput,
} from "react-native";
import Icon from "react-native-vector-icons/Entypo";

function MultipleTimeSelectableOptionGroup({
  style,
  setopenDropdown,
  openDropdown,
  id,
  label,
  isRequired,
  myObjProfile,
  setmyObjProfile,
  index,
  e,
}) {
  const options = e.optionsList;
  const scrollViewRef = useRef(); // Create a ref for the ScrollView
  const [scrollPosition, setScrollPosition] = useState(0); // State to keep track of scroll position

  const handleScroll = (event) => {
    // When user scrolls, update the scrollPosition state
    const scrollY = event.nativeEvent.contentOffset.y;
    setScrollPosition(scrollY);
  };

  // Use useEffect to apply the scroll position after each render
  useEffect(() => {
    if (scrollViewRef.current) {
      scrollViewRef.current.scrollTo({ y: scrollPosition, animated: false });
    }
  }, [myObjProfile]); // Only re-run the effect if myObjProfile changes

  // Add this function to your button onPress handlers before you update the state
  const preserveScrollPosition = () => {
    if (scrollViewRef.current) {
      const currentScrollPosition = scrollViewRef.current
        .getScrollResponder()
        .scrollResponderScrollTo({
          x: 0,
          y: scrollPosition,
          animated: false,
        });
      setScrollPosition(currentScrollPosition);
    }
  };

  const onMinusPress = ({ option, listIndex }) => {
    // Store the current scroll position

    // ...rest of your minus logic
    const newMyObjProfile = structuredClone(myObjProfile);
    //filter out all options[index].optionsList that have selectedTimes > 0 then map through and multiply by countsAs
    const thisItemCountsAs = option.countsAs ? parseInt(option.countsAs) : 1;

    if (
      newMyObjProfile.options[index].optionsList[listIndex].selectedTimes > 0
      ) {
        newMyObjProfile.options[index].optionsList[listIndex].selectedTimes -=
        1 * thisItemCountsAs;
      }

      preserveScrollPosition();
    setmyObjProfile(newMyObjProfile);
  };

  const onPlusPress = ({ option, listIndex }) => {
    // Store the current scroll position
    scrollViewRef.current?.getScrollResponder().scrollResponderScrollTo({
      x: 0,
      y: scrollViewRef.current.scrollY,
      animated: false,
    });

    // ...rest of your plus logic
    const newMyObjProfile = structuredClone(myObjProfile);
    //filter out all options[index].optionsList that have selectedTimes > 0 then map through and multiply by countsAs

    const selectedItems = newMyObjProfile.options[index].optionsList.filter(
      (op) => op.selectedTimes > 0
    );

    const thisItemCountsAs = option.countsAs ? parseInt(option.countsAs) : 1;

    let selectedTimesTotal = thisItemCountsAs;

    selectedItems.map((op) => {
      selectedTimesTotal += op.countsAs
        ? parseInt(op.selectedTimes) * parseInt(op.countsAs)
        : parseInt(op.selectedTimes);
    });

    if (
      parseInt(e.numOfSelectable) >= selectedTimesTotal ||
      !e.numOfSelectable ||
      parseInt(e.numOfSelectable) === 0
    ) {
      if (newMyObjProfile.options[index].optionsList[listIndex].selectedTimes) {
        newMyObjProfile.options[index].optionsList[
          listIndex
        ].selectedTimes += 1;
      } else {
        newMyObjProfile.options[index].optionsList[listIndex].selectedTimes = 1;
      }
      setmyObjProfile(newMyObjProfile);
    } else {
      console.log(
        "Didnt Work ",
        "selectedTimesTotal: ",
        selectedTimesTotal,
        " e.numOfSelectable: ",
        e.numOfSelectable
      );
    }
  };

  return (
    <View
      style={[styles.container, style, openDropdown === id && { zIndex: 1000 }]}
    >
      <Text style={styles.lbl}>
        {label} {isRequired ? "*" : ""}
      </Text>
      <View>
        <TouchableOpacity
          style={styles.dropdown}
          onPress={() => {
            console.log("openDropdown", openDropdown, id);
            if (openDropdown === id) {
              setopenDropdown(null);
            } else {
              setopenDropdown(id);
            }
          }}
        >
          <Text style={styles.placeholder}>Select {label}</Text>
          <Icon
            name={
              openDropdown === id ? "chevron-small-up" : "chevron-small-down"
            }
            style={styles.downIcon}
          ></Icon>
        </TouchableOpacity>
        {openDropdown === id && (
          <ScrollView
            onScroll={handleScroll}
            scrollEventThrottle={16} // Adjust as needed for performance
            style={{
              width: 352,
              position: "absolute",
              backgroundColor: "white",
              bottom: options.length > 3 ? -44 * 3 : -44 * options.length,
              height: options.length > 3 ? 44 * 3 : 44 * options.length,
            }}
          >
            {options.map((option, listIndex) => (
              <View
                key={listIndex}
                id={option.id}
                style={{
                  width: "100%",
                  height: 44,
                  backgroundColor: "white",
                  padding: 10,
                  borderWidth: 1,
                  justifyContent: "space-between",
                  alignItems: "center",
                  flexDirection: "row",
                }}
              >
                <TouchableOpacity
                  onPress={() => onMinusPress({ option, listIndex })}
                >
                  <Text>-</Text>
                </TouchableOpacity>
                <View
                  style={{
                    flexDirection: "row",
                    alignItems: "center",
                    justifyContent: "space-between",
                    width: "80%",
                  }}
                >
                  <Text>
                    {`${option.label} ${
                      option.priceIncrease ? `(+$${option.priceIncrease})` : ""
                    }`}
                  </Text>
                  <TextInput
                    style={{
                      width: 40,
                      height: 30,
                      borderWidth: 1,
                      borderColor: "black",
                      margin: 10,
                    }}
                    value={
                      myObjProfile.options[index].optionsList[listIndex]
                        .selectedTimes > 0
                        ? myObjProfile.options[index].optionsList[listIndex]
                            .selectedTimes
                        : 0
                    }
                    keyboardType="numeric"
                    // onChangeText={(text) => {
                    //   let newOptions = [...options];
                    //   newOptions[index].selectedTimes = text;
                    //   setoptions(newOptions);
                    // }}
                  />
                </View>
                <TouchableOpacity
                  onPress={() => onPlusPress({ option, listIndex })}
                >
                  <Text>+</Text>
                </TouchableOpacity>
              </View>
            ))}
          </ScrollView>
        )}
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
  },
  lbl: {
    fontWeight: "700",
    color: "#3e3f41",
  },
  dropdown: {
    width: 352,
    height: 44,
    backgroundColor: "rgba(255,255,255,1)",
    borderRadius: 10,
    borderWidth: 2,
    borderColor: "#6987d3",
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "flex-start",
  },
  placeholder: {
    fontWeight: "500",
    color: "#7f838c",
    fontSize: 12,
    margin: 10,
  },
  downIcon: {
    color: "rgba(128,128,128,1)",
    fontSize: 30,
    margin: 0,
    marginTop: 2,
    marginRight: 2,
  },
});

export default MultipleTimeSelectableOptionGroup;
