import {
  FlatList,
  ScrollView,
  StyleSheet,
  Text,
  TouchableOpacity,
  View,
} from "react-native";
import React, { useEffect, useRef, useState } from "react";
import { Button, TextInput } from "@react-native-material/core";
import DropDown from "./DropDown";
import { userStoreState } from "state/state";
import { updateData } from "state/firebaseFunctions";

const RenderItem = ({e, index}) => {
            const [optionLblsValues, setoptionLblsValues] = useState([]);
            const [optionLbls, setoptionLbls] = useState([]);

            useEffect(() => {
              //setnewProduct(newProductSaved.current);
              if (newProduct.options.length > 1) {
                const optionLblsLocal = [];
                newProduct.options.forEach((checkUseE) => {
                  if (checkUseE.label !== e.label)
                    optionLblsLocal.push(checkUseE.label);
                });
                setoptionLbls(optionLblsLocal);
              }
            }, []);

            useEffect(() => {
              if (e.selectedCaseKey !== null) {
                const local = newProduct.options.filter(
                  (localE) => localE.label == e.selectedCaseKey
                );
                const optionLblsValuesLocal = [];
                local[0].optionsList.forEach((eVals) => {
                  optionLblsValuesLocal.push(eVals.label);
                });
                setoptionLblsValues(optionLblsValuesLocal);
              }
            }, [optionLbls]);

            return (
              <View
                style={{
                  padding: 25,
                  margin: 15,
                  backgroundColor: "lightgrey",
                }}
              >
                <TouchableOpacity
                  onPress={() => {
                    const copy = structuredClone(newProduct);
                    copy.options.push({
                      ...e,
                      label: e.label + " Copy",
                    });
                    setautoFocusOn({
                      index: null,
                      inputName: null,
                      innerList: null,
                      cursor: null,
                      cursorEnd: null,
                    });
                    setnewProduct((prevState) => ({
                      ...prevState,
                      options: copy.options,
                    }));
                  }}
                >
                  <Text
                    style={{
                      marginBottom: 25,
                      fontWeight: "700",
                      color: "red",
                    }}
                  >
                    Copy
                  </Text>
                </TouchableOpacity>
                <TouchableOpacity
                  onPress={() => {
                    const copy = structuredClone(newProduct);
                    copy.options.splice(index, 1);

                    setnewProduct((prevState) => ({
                      ...prevState,
                      options: copy.options,
                    }));
                  }}
                >
                  <Text
                    style={{
                      marginBottom: 25,
                      fontWeight: "700",
                      color: "red",
                    }}
                  >
                    X
                  </Text>
                </TouchableOpacity>
                <TextInput
                  placeholder="Enter Select List Label"
                  onChange={(e) => {
                    const val = e.nativeEvent.text;
                    const newOptionsList = [...newProduct.options];
                    newOptionsList[index].label = val;
                    setautoFocusOn({
                      index: index,
                      inputName: "label",
                      innerList: false,
                      cursor: e.target.selectionStart,
                      cursorEnd: e.target.selectionEnd,
                    });
                    // sessionStorage.setItem("scrollPosition", window.pageYOffset);
                    setnewProduct((prevState) => ({
                      ...prevState,
                      options: newOptionsList,
                    }));
                  }}
                  onFocus={(e) => {
                    e.target.selectionStart = autoFocusOn.cursor;
                    e.target.selectionEnd = autoFocusOn.cursorEnd;
                  }}
                  value={e.label}
                  autoFocus={
                    autoFocusOn.index === index &&
                    autoFocusOn.innerList === false &&
                    autoFocusOn.inputName === "label"
                  }
                  style={{ marginBottom: 25 }}
                />
                <TextInput
                  placeholder="Enter Number Of Selectable; If There Is"
                  onChange={(e) => {
                    const val = e.nativeEvent.text;
                    const newOptionsList = [...newProduct.options];
                    newOptionsList[index].numOfSelectable = val;
                    setautoFocusOn({
                      index: index,
                      inputName: "numOfSelectable",
                      innerList: false,
                      cursor: e.target.selectionStart,
                      cursorEnd: e.target.selectionEnd,
                    });
                    // sessionStorage.setItem("scrollPosition", window.pageYOffset);
                    setnewProduct((prevState) => ({
                      ...prevState,
                      options: newOptionsList,
                    }));
                  }}
                  onFocus={(e) => {
                    e.target.selectionStart = autoFocusOn.cursor;
                    e.target.selectionEnd = autoFocusOn.cursorEnd;
                  }}
                  value={e.numOfSelectable}
                  autoFocus={
                    autoFocusOn.index === index &&
                    autoFocusOn.innerList === false &&
                    autoFocusOn.inputName === "numOfSelectable"
                  }
                  style={{ marginBottom: 25 }}
                />
                {e.optionsList.map((eInnerList, indexInnerList) => {
                  return (
                    <View
                      style={{
                        flexDirection: "row",
                        marginBottom: 25,
                        alignItems: "center",
                      }}
                    >
                      <TextInput
                        placeholder="Enter Option Label"
                        onChange={(e) => {
                          const val = e.nativeEvent.text;
                          const newOptionsList = structuredClone(newProduct);
                          newOptionsList.options[index].optionsList[
                            indexInnerList
                          ].label = val;
                          setautoFocusOn({
                            index: index,
                            inputName: "label",
                            innerList: true,
                            indexInnerList: indexInnerList,
                            cursor: e.target.selectionStart,
                            cursorEnd: e.target.selectionEnd,
                          });
                          // sessionStorage.setItem(
                          //   "scrollPosition",
                          //   window.pageYOffset
                          // );
                          setnewProduct(newOptionsList);
                        }}
                        onFocus={(e) => {
                          e.target.selectionStart = autoFocusOn.cursor;
                          e.target.selectionEnd = autoFocusOn.cursorEnd;
                        }}
                        value={eInnerList.label}
                        autoFocus={
                          autoFocusOn.index === index &&
                          autoFocusOn.innerList === true &&
                          autoFocusOn.inputName === "label" &&
                          autoFocusOn.indexInnerList === indexInnerList
                        }
                      />
                      <TextInput
                        placeholder="Enter price increase"
                        onChange={(e) => {
                          const val = e.nativeEvent.text;
                          const newOptionsList = structuredClone(newProduct);
                          newOptionsList.options[index].optionsList[
                            indexInnerList
                          ].priceIncrease = val;
                          setautoFocusOn({
                            index: index,
                            inputName: "priceIncrease",
                            innerList: true,
                            indexInnerList: indexInnerList,
                            cursor: e.target.selectionStart,
                            cursorEnd: e.target.selectionEnd,
                          });
                          // sessionStorage.setItem(
                          //   "scrollPosition",
                          //   window.pageYOffset
                          // );
                          setnewProduct(newOptionsList);
                        }}
                        onFocus={(e) => {
                          e.target.selectionStart = autoFocusOn.cursor;
                          e.target.selectionEnd = autoFocusOn.cursorEnd;
                        }}
                        value={eInnerList.priceIncrease}
                        autoFocus={
                          autoFocusOn.index === index &&
                          autoFocusOn.innerList === true &&
                          autoFocusOn.inputName === "priceIncrease" &&
                          autoFocusOn.indexInnerList === indexInnerList
                        }
                        style={{ marginLeft: 20, marginRight: 20 }}
                      />
                      <TouchableOpacity
                        onPress={() => {
                          const copy = structuredClone(newProduct);
                          copy.options[index].optionsList.splice(
                            indexInnerList,
                            1
                          );

                          setnewProduct((prevState) => ({
                            ...prevState,
                            options: copy.options,
                          }));

                          setnewProduct((prevState) => ({
                            ...prevState,
                            options: copy.options,
                          }));
                        }}
                      >
                        <Text
                          style={{
                            fontWeight: "700",
                            color: "red",
                          }}
                        >
                          X
                        </Text>
                      </TouchableOpacity>
                    </View>
                  );
                })}
                <Button
                  title="Add Option Choice"
                  onPress={() => {
                    const newOptionsList = [...newProduct.options];
                    newOptionsList[index].optionsList.push({
                      label: null,
                      priceIncrease: null,
                    });
                    setnewProduct((prevState) => ({
                      ...prevState,
                      options: newOptionsList,
                    }));
                    setautoFocusOn({
                      index: index,
                      inputName: "label",
                      innerList: true,
                      indexInnerList:
                        newOptionsList[index].optionsList.length - 1,
                    });
                    // sessionStorage.setItem("scrollPosition", window.pageYOffset);
                  }}
                  style={{ marginBottom: 25 }}
                />
                {optionLbls.length > 1 && (
                  <View
                    style={{
                      flexDirection: "row",
                      justifyContent: "space-between",
                      marginBottom: 25,
                    }}
                  >
                    <DropDown
                      label="Show if..."
                      options={optionLbls}
                      setValue={(val) => {
                        const newOptionsList = [...newProduct.options];
                        newOptionsList[index].selectedCaseKey = val;
                        setnewProduct((prevState) => ({
                          ...prevState,
                          options: newOptionsList,
                        }));
                        // sessionStorage.setItem(
                        //   "scrollPosition",
                        //   window.pageYOffset
                        // );
                        setautoFocusOn({
                          index: null,
                          inputName: null,
                          innerList: null,
                          indexInnerList: null,
                        });
                      }}
                      value={e.selectedCaseKey}
                      style={{ marginBottom: 25 }}
                    />
                    <Text>"="</Text>
                    <DropDown
                      label="Show if..."
                      options={optionLblsValues}
                      setValue={(val) => {
                        const newOptionsList = [...newProduct.options];
                        newOptionsList[index].selectedCaseValue = val;
                        setnewProduct((prevState) => ({
                          ...prevState,
                          options: newOptionsList,
                        }));
                        // sessionStorage.setItem(
                        //   "scrollPosition",
