import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import Swal from "sweetalert2";
import Table from "../../EntryFile/datatable";
import Tabletop from "../../EntryFile/tabletop";
import {
  PlusIcon,
  MacbookIcon,
  IphoneIcon,
  SamsungIcon,
  EarpodIcon,
  OrangeImage,
  PineappleImage,
  StawberryImage,
  AvocatImage,
  EyeIcon,
  EditIcon,
  DeleteIcon,
  search_whites,
  DuplicateIcon,
} from "../../EntryFile/imagePath";
import Select2 from "react-select2-wrapper";
import "react-select2-wrapper/css/select2.css";
import { setSelectedProductState, userStoreState } from "state/state";
import { Button } from "react-native";
import { updateData } from "state/firebaseFunctions";

const ProductList = () => {
  const catalog = userStoreState.use();
  const [inputfilter, setInputfilter] = useState(false);

  //select2
  const options = [
    { id: 1, text: "Choose Product", text: "Choose Product" },
    { id: 2, text: "Macbook pro", text: "Macbook pro" },
    { id: 3, text: "Orange", text: "Orange" },
  ];
  const options2 = [
    { id: 1, text: "Choose Category", text: "Choose Category" },
    { id: 2, text: "Computers", text: "Computers" },
    { id: 3, text: "Fruits", text: "Fruits" },
  ];
  const options3 = [
    { id: 1, text: "Choose Sub Category", text: "Choose Sub Category" },
    { id: 2, text: "Computers", text: "Computers" },
  ];
  const options4 = [
    { id: 1, text: "Brand", text: "Brand" },
    { id: 2, text: "N/D", text: "N/D" },
  ];
  const options5 = [
    { id: 1, text: "Price", text: "Price" },
    { id: 2, text: "150.00", text: "150.00" },
  ];

  const togglefilter = (value) => {
    setInputfilter(value);
  };
  const confirmText = (props) => {
    console.log("COnfirm Text: ", props);
    Swal.fire({
      title: "Are you sure?",
      text: "You won't be able to revert this!",
      type: "warning",
      showCancelButton: !0,
      confirmButtonColor: "#3085d6",
      cancelButtonColor: "#d33",
      confirmButtonText: "Yes, delete it!",
      confirmButtonClass: "btn btn-primary",
      cancelButtonClass: "btn btn-danger ml-1",
      buttonsStyling: !1,
    }).then(function (t) {
      if (t.value) {
        Swal.fire({
          type: "success",
          title: "Deleted!",
          text: "Your file has been deleted.",
          confirmButtonClass: "btn btn-success",
        });
        const localCatalog = structuredClone(catalog);
        if (localCatalog.products.length > 1) {
          localCatalog.products.splice(props.id - 1, 1);
        } else {
          localCatalog.products = [];
        }

        updateData(localCatalog.categories, localCatalog.products);
      }
    });
  };

  const [data, setData] = useState([]);

  useEffect(() => {
    if (catalog.products.length > 0) {
      catalog.products.map((product, index) => {
        setData((prev) => [
          ...prev,
          {
            id: index + 1,
            image: AvocatImage,
            productName: product.name,
            // sku: "PT0012",
            category: product.catagory ? product.catagory : product.category,
            // brand: "N/D",
            price: product.price,
            // unit: "N/D",
            // qty: "N/D",
            createdBy: "Admin",
          },
        ]);
      });
    }
  }, []);

  const columns = [
    {
      title: "Product Name",
      dataIndex: "productName",
      render: (text, record) => (
        <div className="productimgname">
          <Link
            style={{ textDecoration: "none" }}
            className="product-img"
            to="/authed/product/editproduct-product"
            onClick={() =>
              setSelectedProductState({
                existingProduct: catalog.products[record.id - 1],
                existingProductIndex: record.id - 1,
              })
            }
          >
            <img alt="" src={record.image} />
          </Link>
          <Link
            style={{ textDecoration: "none" }}
            style={{ fontSize: "15px", marginLeft: "10px" }}
            to="/authed/product/editproduct-product"
            onClick={() =>
              setSelectedProductState({
                existingProduct: catalog.products[record.id - 1],
                existingProductIndex: record.id - 1,
              })
            }
          >
            {record.productName}
          </Link>
        </div>
      ),
      sorter: (a, b) => a.productName.length - b.productName.length,
    },
    // {
    //   title: "SKU",
    //   dataIndex: "sku",
    //   sorter: (a, b) => a.sku.length - b.sku.length,
    // },
    {
      title: "Category",
      dataIndex: "category",
      sorter: (a, b) => a.category.length - b.category.length,
    },
    // {
    //   title: "Brand",
    //   dataIndex: "brand",
    //   sorter: (a, b) => a.brand.length - b.brand.length,
    // },
    {
      title: "Price",
      dataIndex: "price",
      sorter: (a, b) => a.price.length - b.price.length,
    },
    // {
    //   title: "Unit",
    //   dataIndex: "unit",
    //   sorter: (a, b) => a.unit.length - b.unit.length,
    // },
    // {
    //   title: "Qty",
    //   dataIndex: "qty",
    //   sorter: (a, b) => a.qty.length - b.qty.length,
    // },
    {
      title: "Created By",
      dataIndex: "createdBy",
      sorter: (a, b) => a.createdBy.length - b.createdBy.length,
    },
    {
      title: "Action",
      render: (props) => (
        <>
          <>
            <Link
              style={{ textDecoration: "none" }}
              className="me-3"
              // to="/authed/product/product-details"
              onClick={() => {
                let copy = structuredClone(catalog.products[props.id - 1]);
                copy.name = copy.name + " Copy";
                copy.id = Math.random().toString(36).substr(2, 9);
                updateData(
                  [...catalog.categories],
                  [...catalog.products, copy]
                );
              }}
            >
              <img src={DuplicateIcon} alt="img" />
            </Link>
            <Link
              style={{ textDecoration: "none" }}
              className="me-3"
              to="/authed/product/editproduct-product"
              onClick={() =>
                setSelectedProductState({
                  existingProduct: catalog.products[props.id - 1],
                  existingProductIndex: props.id - 1,
                })
              }
            >
              <img src={EditIcon} alt="img" />
            </Link>
            <Link
              style={{ textDecoration: "none" }}
              className="confirm-text"
              to="#"
              onClick={() => confirmText(props)}
            >
              <img src={DeleteIcon} alt="img" />
            </Link>
          </>
        </>
      ),
    },
  ];

  return (
    <>
      <div className="page-wrapper">
        <div className="content">
          <div className="page-header">
            <div className="page-title">
              <h4>Product List</h4>
              <h6>Manage your products</h6>
            </div>
            <div className="page-btn">
              <Link
                style={{ textDecoration: "none" }}
                to="/authed/product/addproduct-product"
                className="btn btn-added"
              >
                <img src={PlusIcon} alt="img" className="me-1" />
                Add New Product
              </Link>
            </div>
          </div>
          {/* /product list */}
          <div className="card">
            <div className="card-body">
              <Tabletop inputfilter={inputfilter} togglefilter={togglefilter} />
              {/* /Filter */}
              <div
                className={`card mb-0 ${inputfilter ? "toggleCls" : ""}`}
                id="filter_inputs"
                style={{ display: inputfilter ? "block" : "none" }}
              >
                <div className="card-body pb-0">
                  <div className="row">
                    <div className="col-lg-12 col-sm-12">
                      <div className="row">
                        <div className="col-lg col-sm-6 col-12">
                          <div className="form-group">
                            <Select2
                              className="select"
                              data={options}
                              options={{
                                placeholder: "Choose Product",
                              }}
                            />
                          </div>
                        </div>
                        <div className="col-lg col-sm-6 col-12">
                          <div className="form-group">
                            <Select2
                              className="select"
                              data={options2}
                              options={{
                                placeholder: "Choose Category",
                              }}
                            />
                          </div>
                        </div>
                        <div className="col-lg col-sm-6 col-12">
                          <div className="form-group">
                            <Select2
                              className="select"
                              data={options3}
                              options={{
                                placeholder: "Choose Sub Category",
                              }}
                            />
                          </div>
                        </div>
                        <div className="col-lg col-sm-6 col-12">
                          <div className="form-group">
                            <Select2
                              className="select"
                              data={options4}
                              options={{
                                placeholder: "Brand",
                              }}
                            />
                          </div>
                        </div>
                        <div className="col-lg col-sm-6 col-12 ">
                          <div className="form-group">
                            <Select2
                              className="select"
                              data={options5}
                              options={{
                                placeholder: "Price",
                              }}
                            />
                          </div>
                        </div>
                        <div className="col-lg-1 col-sm-6 col-12">
                          <div className="form-group">
                            <a className="btn btn-filters ms-auto">
                              <img src={search_whites} alt="img" />
                            </a>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
              {/* /Filter */}
              <div className="table-responsive">
                <Table columns={columns} dataSource={data} />
              </div>
            </div>
          </div>
          {/* /product list */}
        </div>
      </div>
    </>
  );
};
export default ProductList;
